package operators;

public class SwitchCaseStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int dayOfTheWeek=2;
		switch(dayOfTheWeek)
		{
		case 1:
			System.out.println("im not happy.");
			break;
		case 2:
			System.out.println("im avaerage");
			break;
		case 3:
			System.out.println("im little bit excited");
			break;
		case 4:
			System.out.println("im quite excited");
			break;
		case 5:
			System.out.println("im happy bcoz weekend gonna statrt now ");
			break;
			default:
				System.out.println("Weekend is on bitch");
		}

	}

}
